var scoreboard = {states: {}, timeouts: {}};
$(function () {
    scoreboard.binder()
}), scoreboard.binder = function () {
    $("#scoreboard, #scorelist").on("click", ".score-item", function () {
        var o = $(this).next(".team-detail"), s = $(this).attr("id");
        scoreboard.states[s] ? (o.stop().slideUp(), scoreboard.states[s] = !1, clearTimeout(scoreboard.timeouts[s])) : (o.stop().slideDown(), scoreboard.states[s] = !0, scoreboard.timeouts[s] = setTimeout(function () {
            o.stop().slideUp(), scoreboard.states[s] = !1
        }, 4e3))
    })
};